<template>
  <div :class="wrapperClass" v-bind="attrs">
    <div v-if="notifications.length" :class="ui.container">
      <div v-for="notification of notifications" :key="notification.id">
        <UNotification
          v-bind="notification"
          :class="notification.click && 'cursor-pointer'"
          @click="notification.click && notification.click(notification)"
          @close="toast.remove(notification.id)"
        >
          <template v-for="(_, name) in $slots" #[name]="slotData">
            <slot :name="name" v-bind="slotData" />
          </template>
        </UNotification>
      </div>
    </div>
  </div>
</template>

<script>
import { computed, defineComponent } from "vue";
import { omit } from "lodash-es";
import { twMerge, twJoin } from "tailwind-merge";
import UNotification from "./Notification.vue";
import { useToast } from "../../composables/useToast";
import { defuTwMerge } from "../../utils";
import { useState, useAppConfig } from "#imports";
export default defineComponent({
  components: {
    UNotification
  },
  inheritAttrs: false,
  props: {
    ui: {
      type: Object,
      default: () => ({})
    }
  },
  setup(props, { attrs }) {
    const appConfig2 = useAppConfig();
    const ui = computed(() => defuTwMerge({}, props.ui, appConfig2.ui.notifications));
    const toast = useToast();
    const notifications = useState("notifications", () => []);
    const wrapperClass = computed(() => {
      return twMerge(twJoin(
        ui.value.wrapper,
        ui.value.position,
        ui.value.width
      ), attrs.class);
    });
    return {
      attrs: omit(attrs, ["class"]),
      // eslint-disable-next-line vue/no-dupe-keys
      ui,
      toast,
      notifications,
      wrapperClass
    };
  }
});
</script>
